	/**
	 * An array of articles
	 */
	private String[] articles = {
			"the", "a"
	};

	/**
	 * An array of punctuation marks
	 */
	private String[] prepositions = {
			"", "on", "of", "from", "at", "towards", "in front of", "behind"
	};
	
	/**
	 * An array of punctuation marks
	 */
	private String[] punctuations = {
			".", "?", "!", "..."
	};


	/**
	 * Get a poem of a particular number of lines.
	 * @param numLines The number of lines of the poem to get
	 * @return The poem, as a String
	 */
	public String getAPoem(int numLines) {

		// create a new blank string builder
		StringBuilder poem = new StringBuilder("");
		
		// loop as many times as we have lines
		for (int i=0; i<numLines; i++ ) {
			poem.append(this.getALine());
		}
		
		return poem.toString();
	}
	
	/**
	 * Generate a line of poetry.
	 * @return A line of poetry.
	 */
	public String getALine() {

		// create a new blank string builder
		StringBuilder line = new StringBuilder("");

		// create a random number generator
		Random randy = new Random();
		
		// randomly pick a noun
		String noun = nouns[randy.nextInt(nouns.length)]; // grab one of the nouns from the array
		
		// capitalize the noun
		char[] characters = noun.toCharArray(); // get the char array from this string
		char c = Character.toUpperCase(characters[0]);
		characters[0] = c; // replace the first character in the word with its uppercase equivalent
		noun = new String(characters); // make a new string from this char array
		
		// append this noun to the poem
		line.append(" " + noun);  // does not create a new object!
		
		// randomly pick a adverb
		String adverb = adverbs[randy.nextInt(adverbs.length)]; // grab one of the adverbs from the array
		line.append(" " + adverb); // append this adverb to the poem

		// randomly pick a verb
		String verb = verbs[randy.nextInt(verbs.length)]; // grab one of the verbs from the array
		line.append(" " + verb); // append this verb to the poem

		// randomly pick a preposition
		String preposition = prepositions[randy.nextInt(prepositions.length)]; // grab one of the prepositions from the array
		line.append(" " + preposition); // append this article to the poem

		// randomly pick an article
		String article = articles[randy.nextInt(articles.length)]; // grab one of the articles from the array
		line.append(" " + article); // append this article to the poem

		// randomly pick an adjective
		String adj = adjectives[randy.nextInt(adjectives.length)]; // grab one of the adjectives from the array
		line.append(" " + adj); // append this adjective to the poem

		// randomly pick a noun object
		String object = nouns[randy.nextInt(nouns.length)]; // grab one of the nouns from the array
		line.append(" " + object); // append this noun to the poem
		
		// add a random punctuation mark at the end
		String punctuation = punctuations[randy.nextInt(punctuations.length)]; // grab one of the punctuation marks from the array
		line.append(punctuation); // append this punctuation mark to the poem
		
		// add a line break at the end of the sentence
		line.append("\n");
			
		// return the String representation of this StringBuilder
		return line.toString();

	}

